function dataFormat(datastr) {
    const dt = new Date(datastr);
    const y = dt.getFullYear();
    const m = ParseZero(dt.getMonth() + 1);
    const d = ParseZero(dt.getDate());
    const hh = ParseZero(dt.getHours());
    const mm = ParseZero(dt.getMinutes());
    const ss = ParseZero(dt.getSeconds());

    return `${y}-${m}-${d} ${hh}:${mm}:${ss}`;
}

function ParseZero(n) {
    return n > 9 ? n : '0' + n;
}

var dataformat = {
    dataFormat,
};

// 定义转义 HTML 字符的函数
function htmlEscape(htmlstr) {
    return htmlstr.replace(/<|>|"|&/g, match => {
        switch (match) {
            case '<':
                return '&lt;'
            case '>':
                return '&gt;'
            case '"':
                return '&quot;'
            case '&':
                return '&amp;'
        }
    })
}

// 定义还原 HTML 字符串的函数
function htmlUnEscape(str) {
    return str.replace(/&lt;|&gt;|&quot;|&amp;/g, match => {
        switch (match) {
            case '&lt;':
                return '<'
            case '&gt;':
                return '>'
            case '&quot;':
                return '"'
            case '&amp;':
                return '&'
        }
    })
}

var htmlEscape_1 = {
    htmlEscape,
    htmlUnEscape
};

//包入口文件


//暴露函数
var yetiTool = {
  ...dataformat,
  ...htmlEscape_1
};

export { yetiTool as default };
