function dataFormat(datastr) {
    const dt = new Date(datastr);
    const y = dt.getFullYear();
    const m = ParseZero(dt.getMonth() + 1);
    const d = ParseZero(dt.getDate());
    const hh = ParseZero(dt.getHours());
    const mm = ParseZero(dt.getMinutes());
    const ss = ParseZero(dt.getSeconds());

    return `${y}-${m}-${d} ${hh}:${mm}:${ss}`;
}

function ParseZero(n) {
    return n > 9 ? n : '0' + n;
}

module.exports = {
    dataFormat,
}
  